mapper.select:
SELECT u.user_id , u.user_nickname , u.id , u.profile_pic , u.bio FROM follow f
 INNER JOIN user u ON f.follow_id = u.id
WHERE f.user_id = #{userId}

SELECT F.follow_id, U.user_id, U.user_nickname, U.id, U.profile_pic, U.bio
FROM follow F
         INNER JOIN USER U ON F.follow_id = U.ID
WHERE F.USER_ID = #{id}
  AND EXISTS (
        SELECT 1 FROM follow F2
        WHERE F2.USER_ID = #{userId} AND F2.follow_id = F.follow_id
    );

