FROM follow F
         INNER JOIN USER U ON F.follow_id = U.ID
WHERE F.USER_ID = #{id}
  AND NOT EXISTS (
        SELECT 1 FROM follow F2
        WHERE F2.USER_ID = #{userId} AND F2.follow_id = F.follow_id
    );

